        '外観の印象': { type: 'multi', options: ['1.若い', '2.軽い', '3.濃厚な', '4.良く熟した', '5.成熟度がとても高い', '6.濃縮感のある', '7.やや熟成した', '8.熟成した', '9.酸化熟成のニュアンス', '10.酸化が進んだ', '11.完全に酸化している'], maxSelect: 4 },
    },

    // === 香り (嗅覚による考察) ===
    Aroma: {
        title: '香り',
        // ※「第一印象」は修正案を適用し、5と6を整理
        '第一印象': { type: 'multi', options: ['1.閉じている', '2.控えめ', '3.開いている', '4.フレッシュな', '5.チャーミングな', '6.はなやか', '7.熟度の高い', '8.濃縮感のある', '9.深みのある', '10.発展的な', '11.熟成感のある', '12.複雑', '13.強い'], maxSelect: 4 },

