        '特徴/芳香・化学物質': { type: 'multi', options: ['1.貝殻', '2.石灰', '3.火打ち石', '4.鉱物', '5.海の香り', '6.トースト', '7.ヴァニラ', '8.コリアンダー', '9.白胡椒', '10.クローヴ(丁子)', '11.香木', '12.お香', '13.ジンジャーブレッド', '14.硫黄', '15.パン・ド・ミ', '16.ハチミツ', '17.乳製品', '18.フェノール', '19.麝香', '20.花の蜜'], maxSelect: 4 }, // 選択数: 最大4
